import { STORE_IN_PROCESS } from '../actions/actionsType';

const INITIAL_STATE = {
  inProgress: {},
};

const inProcessReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case STORE_IN_PROCESS:
    return {
      inProgress: action.payload,
    };
  default:
    return state;
  }
};

export default inProcessReducer;
