import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import PropTypes from 'prop-types';
import HeaderDetails from '../../components/HeaderDetails/HeaderDetails';
import { actionRequestItems } from '../../actions';
import { fetchApi } from '../../components/SearchBar/utils';
import { ingredientsMeasuresFunc } from './utils';

const DrinkProcess = ({ match: { params: { id } } }) => {
  const dispatch = useDispatch();
  const [itensInfo, setItensInfo] = useState({
    image: '',
    title: '',
    category: '',
    instructions: '',
    ingredients: [],
  });

  useEffect(() => {
    const fetchItem = async () => {
      dispatch(actionRequestItems());
      try {
        const response = await fetchApi(`https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=${id}`);
        const {
          strDrink,
          strCategory,
          strDrinkThumb,
          strInstructions,
        } = response.drinks[0];
        setItensInfo({
          image: strDrinkThumb,
          title: strDrink,
          category: strCategory,
          instructions: strInstructions,
          ingredients: ingredientsMeasuresFunc(response),
        });
      } catch (error) {
        console.log(`Erro ao carregar as primeiras receitas: ${error}`);
      }
    };
    fetchItem();
  }, [dispatch, id]);

  return (
    <div>
      <div>
        <HeaderDetails
          image={ itensInfo.image }
          title={ itensInfo.title }
          category={ itensInfo.category }
        />
      </div>
      <div>
        <h3>Ingredients</h3>
        <div>
          {
            itensInfo.ingredients.map((ing, index) => (
              <div
                key={ ing }
                data-testid={ `data-testid=${index}-ingredient-step` }
              >
                <input
                  type="checkbox"
                />
                {ing}
              </div>
            ))
          }
        </div>
      </div>
      <div>
        <h3>Instructions</h3>
        <p
          data-testid="instructions"
        >
          {itensInfo.instructions}
        </p>
      </div>
      <div>
        <button
          type="button"
          data-testid="finish-recipe-btn"
        >
          Finalizar Receita
        </button>
      </div>
    </div>
  );
};

DrinkProcess.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      id: PropTypes.node.isRequired,
    }).isRequired,
  }).isRequired,
};

export default DrinkProcess;
