import React from 'react';
import PropTypes from 'prop-types';

export default class ShoppingCart extends React.Component {
  render() {
    const { itemList } = this.props;
    if (itemList.length === 0) {
      return <h1 data-testid="shopping-cart-empty-message">Seu carrinho est√° vazio</h1>;
    }
    return (
      <div>
        <p data-testid="shopping-cart-product-quantity">{itemList.length}</p>
        {itemList.map(({ title, thumbnail, price }) => (
          <div className="card" key="title">
            <p
              className="card-title"
              data-testid="shopping-cart-product-name"
            >
              { title }
            </p>
            <img className="card-image" src={ thumbnail } alt={ title } />
            <p>{`R$: ${price}`}</p>
          </div>
        ))}
      </div>
    );
  }
}

ShoppingCart.propTypes = {
  itemList: PropTypes.arrayOf(Array).isRequired,
};
