// implement SearchBar component here
import React from 'react';
import PropTypes from 'prop-types';
import Input from './Input';

class SearchBar extends React.Component {
  render() {
    const {
      searchText,
      onSearchTextChange,
      bookmarkedOnly,
      onBookmarkedChange,
    } = this.props;
    return (
      <form data-testid="search-bar-form">
        <Input
          labelText="Inclui o texto"
          labelId="text-input-label"
          inputId="text-input"
          inputType="text"
          inputValue={ searchText }
          onChangeInput={ onSearchTextChange }
        />
        <Input
          labelText="Mostrar somente favoritos"
          labelId="checkbox-input-label"
          inputId="checkbox-input"
          inputType="checkbox"
          inputValue={ bookmarkedOnly }
          onChangeInput={ onBookmarkedChange }
        />
        <Input
          labelText=""
          labelId="text-input-label"
          inputId="text-input"
          inputType="text"
          inputChecked={ searchText }
          onChangeInput={ onSearchTextChange }
        />
      </form>
    );
  }
}

SearchBar.propTypes = {
  searchText: PropTypes.string.isRequired,
  onSearchTextChange: PropTypes.func.isRequired,
  onBookmarkedChange: PropTypes.func.isRequired,
  bookmarkedOnly: PropTypes.string,

};

export default SearchBar;
