import React, { useContext, useState, useEffect } from 'react';
import StarWarsPlanetsContext from '../../../context/StarWarsPlanetsContext';

const BtnAddFilter = () => {
  const { data, column, comparison,
    value, setState, stateCopy, setStateCopy } = useContext(StarWarsPlanetsContext);
  const [filteredPlanets, setfilteredPlanets] = useState([]);

  // console.log(stateCopy);

  const test = () => {
    setStateCopy(data);
    let doubleFilteredPlanets = [];
    if (comparison === 'maior que') {
      doubleFilteredPlanets = stateCopy.filter((planet) => +planet[column] > +value);
      console.log(doubleFilteredPlanets);
      setfilteredPlanets(doubleFilteredPlanets);
    }

    if (comparison === 'menor que') {
      doubleFilteredPlanets = stateCopy.filter((planet) => +planet[column] < +value);
      setfilteredPlanets(doubleFilteredPlanets);
    }

    if (comparison === 'igual a') {
      doubleFilteredPlanets = stateCopy.filter((planet) => +planet[column] === +value);
      setfilteredPlanets(doubleFilteredPlanets);
    }
  };

  useEffect(() => {
    setState(filteredPlanets);
  }, [filteredPlanets, setState]);

  return (
    <button
      data-testid="button-filter"
      type="button"
      onClick={ () => test() }
    >
      Add Filter
    </button>
  );
};

export default BtnAddFilter;
