import React, { useState, useContext, useEffect } from 'react';
import StarWarsPlanetsContext from '../../context/StarWarsPlanetsContext';

const SearchInput = () => {
  const [state, setState] = useState('');
  const { data, setData } = useContext(StarWarsPlanetsContext);
  const handleInput = ({ target }) => {
    const { value } = target;
    setState(value);
  };

  useEffect(() => {
    const containInput = data
      .filter((planet) => (
        planet.name.includes(state)
      ));
    console.log(containInput);
    setData(containInput);
  }, [state]);

  return (
    <input
      data-testid="name-filter"
      type="text"
      onChange={ handleInput }
    />
  );
};

export default SearchInput;
