import React, { useContext } from 'react';
import Context from '../../../context/Context';

const Table = () => {
  const {
    data,
    filters: {
      filterByName: { input },
      // filterByNumericValue,
    },
    loading,
  } = useContext(Context);

  if (loading || data.length === 0) return <h2>Loading...</h2>;
  const keysWithoutResidents = Object.keys(data[0]).filter((key) => key !== 'residents');

  const filterByName = (planets) => {
    if (planets.length > 0) {
      return planets.filter((planet) => planet.name.includes(input));
    }
  };

  const applyFilters = () => filterByName(data);

  useEffect(
    applyFilters,
    [data, typedName, filterByNumericValue, order],
  );

  return (
    <table>
      <thead>
        <tr>
          {keysWithoutResidents.map((key) => <th key={ key }>{ key }</th>)}
        </tr>
      </thead>
      <tbody>
        {data
          .map((planet) => (
            <tr key={ planet.name }>
              {keysWithoutResidents.map((key) => (
                <td key={ planet[key] }>
                  {planet[key]}
                </td>
              ))}
            </tr>
          ))}
      </tbody>
    </table>
  );
};

export default Table;
