import React, { useState, useEffect } from 'react';
import Proptypes from 'prop-types';
import StarWarsPlanetsContext from './StarWarsPlanetsContext';
import useFetchData from '../hooks/useFetchData';
import SearchInput from '../components/DisplayScreen/SearchInput';

const Provider = ({ children }) => {
  const [data, setData] = useState([]);
  const [state, loading] = useFetchData();
  const [input] = SearchInput();
  useEffect(() => {
    setData(state);
  }, [state]);

  useEffect(() => {
    const containInput = data
      .filter((planet) => (
        planet.name.includes(state)
      ));
  }, [input]);

  const contextValue = {
    data,
    loading,
  };

  return (
    <StarWarsPlanetsContext.Provider value={ contextValue }>
      {children}
    </StarWarsPlanetsContext.Provider>
  );
};

Provider.propTypes = {
  children: Proptypes.node.isRequired,
};

export default Provider;
