import React, { useContext } from 'react';
import Context from '../../../context/Context';

const FiltersInUse = () => {
  const { filters: { filterByNumericValues } } = useContext(Context);
  if (filterByNumericValues.length > 0) {
    return (
      <div>
        <h2>Oi</h2>
      </div>
    );
  }
  return null;
};

export default FiltersInUse;
