import React, { useState } from 'react';
import Proptypes from 'prop-types';
import StarWarsPlanetsContext from './StarWarsPlanetsContext';
import useFetchData from '../hooks/useFetchData';

const Provider = ({ children }) => {
  const options = [
    'population', 'orbital_period', 'diameter', 'rotation_period', 'surface_water',
  ];

  const [columnAlreadyUsed, setColumnAlreadyUsed] = useState([]);
  const [optionsFilter] = useState(options);

  useEffect(() => {
    const getPlanets = async () => {
      const endPoint = 'https://swapi-trybe.herokuapp.com/api/planets/';
      const result = await fetch(endPoint);
      const resultJson = await result.json();
      const { results } = resultJson;
      setState(results);
      setStateCopy(results);
      setLoading(false);
    };

    getPlanets();
  }, []);

  const [
    state, loading, input, setInput,
    column, setColumn, comparison, setComparison,
    numberValue, setNumberValue, setState, stateCopy, setStateCopy,
  ] = useFetchData();

  const contextValue = {
    data: state,
    setState,
    filters: {
      filterByName: {
        name: input,
      },
      filterByNumericValues: [
        {
          column,
          comparison,
          numberValue,
        },
      ],
    },
    optionsFilter,
    loading,
    setInput,
    column,
    setColumn,
    comparison,
    setComparison,
    numberValue,
    setNumberValue,
    stateCopy,
    setStateCopy,
    columnAlreadyUsed,
    setColumnAlreadyUsed,
  };

  return (
    <StarWarsPlanetsContext.Provider value={ contextValue }>
      {children}
    </StarWarsPlanetsContext.Provider>
  );
};

Provider.propTypes = {
  children: Proptypes.node.isRequired,
};

export default Provider;
