import React, { useState } from 'react';
import Proptypes from 'prop-types';
import StarWarsPlanetsContext from './StarWarsPlanetsContext';
import useFetchData from '../hooks/useFetchData';

const Provider = ({ children }) => {
  const optionsFilterArray = [
    'population', 'orbital_period', 'diameter', 'rotation_period', 'surface_water',
  ];
  const [columnAlreadyUsed, setColumnAlreadyUsed] = useState([]);
  const [optionsFilter, setOptionsFilter] = useState(optionsFilterArray);
  const [
    state, loading, input, setInput,
    column, setColumn, comparison, setComparison,
    numberValue, setNumberValue, setState, stateCopy, setStateCopy,
  ] = useFetchData();

  useEffect(() => {
    const filterOptionsFilter = optionsFilter.filter((filter) => (
      filter !== columnAlreadyUsed
    ));
    setOptionsFilter(filterOptionsFilter);
  }, [columnAlreadyUsed, optionsFilter]);

  const contextValue = {
    data: state,
    setState,
    filters: {
      filterByName: {
        name: input,
      },
      filterByNumericValues: [
        {
          column,
          comparison,
          numberValue,
        },
      ],
    },
    optionsFilter,
    loading,
    setInput,
    column,
    setColumn,
    comparison,
    setComparison,
    numberValue,
    setNumberValue,
    stateCopy,
    setStateCopy,
    columnAlreadyUsed,
    setColumnAlreadyUsed,
  };

  return (
    <StarWarsPlanetsContext.Provider value={ contextValue }>
      {children}
    </StarWarsPlanetsContext.Provider>
  );
};

Provider.propTypes = {
  children: Proptypes.node.isRequired,
};

export default Provider;
