import React from 'react';
import useFetchData from '../../hooks/useFetchData';

const SearchInput = () => {
  const { setInput } = useFetchData();

  const handleInput = ({ target }) => {
    const { value } = target;
    setInput(value);
  };

  return (
    <input
      data-testid="name-filter"
      type="text"
      onChange={ handleInput }
    />
  );
};

export default SearchInput;
