import React, { useContext } from 'react';
import StarWarsPlanetsContext from '../../../context/StarWarsPlanetsContext';

const BtnAddFilter = () => {
  const { data, input, column, comparison, value, setState,
    stateCopy } = useContext(StarWarsPlanetsContext);
  const [filteredPlanets, setfilteredPlanets] = useState([]);

  console.log(data);

  if (comparison === 'maior que') {
    const doubleFilteredPlanets = data.filter((planet) => planet[column] > value);
    setfilteredPlanets(doubleFilteredPlanets);
  }

  if (comparison === 'menor que') {
    const doubleFilteredPlanets = data.filter((planet) => planet[column] < value);
    setfilteredPlanets(doubleFilteredPlanets);
  }

  if (comparison === 'igual a') {
    const doubleFilteredPlanets = data.filter((planet) => planet[column] === value);
    setfilteredPlanets(doubleFilteredPlanets);
  }

  return (
    <button
      data-testid="button-filter"
      type="button"
      onClick={ () => setState(filteredPlanets) }
    >
      Add Filter
    </button>
  );
};

export default BtnAddFilter;
