import React, { useState, useEffect } from 'react';
import Proptypes from 'prop-types';
import StarWarsPlanetsContext from './Context';

const Provider = ({ children }) => {
  const options = [
    'population', 'orbital_period', 'diameter', 'rotation_period', 'surface_water',
  ];

  const [state, setState] = useState([]);
  const [stateCopy, setStateCopy] = useState([]);
  const [loading, setLoading] = useState(true);
  const [input, setInput] = useState('');
  const [columnAlreadyUsed, setColumnAlreadyUsed] = useState([]);
  const [optionsFilter] = useState(options);

  useEffect(() => {
    const getPlanets = async () => {
      const endPoint = 'https://swapi-trybe.herokuapp.com/api/planets/';
      const result = await fetch(endPoint);
      const resultJson = await result.json();
      const { results } = resultJson;
      setState(results);
      setStateCopy(results);
      setLoading(false);
    };

    getPlanets();
  }, []);

  useEffect(() => {
    const filteredPlanets = stateCopy.filter((planet) => planet.name.includes(input));
    setState(filteredPlanets);
  }, [input, stateCopy]);

  const contextValue = {
    data: state,
    setState,
    filters: {
      filterByName: {
        name: input,
      },
    },
    optionsFilter,
    loading,
    setInput,
    stateCopy,
    setStateCopy,
    columnAlreadyUsed,
    setColumnAlreadyUsed,
  };

  return (
    <StarWarsPlanetsContext.Provider value={ contextValue }>
      {children}
    </StarWarsPlanetsContext.Provider>
  );
};

Provider.propTypes = {
  children: Proptypes.node.isRequired,
};

export default Provider;
