import React, { useContext, useEffect, useState } from 'react';
import Context from '../../../context/Context';

const Table = () => {
  const {
    data,
    filters: {
      filterByName: { input },
      filterByNumericValue,
    },
    loading,
  } = useContext(Context);

  const [dataToBeUsed, setDataToBeUsed] = useState([]);

  useEffect(() => {
    const filterByName = (planets) => {
      if (planets.length > 0) {
        const filteredPlanets = planets.filter((planet) => planet.name.includes(input));
        setDataToBeUsed(filteredPlanets);
      }
      setDataTobeUsed(planets);
    };
    filterByName(data);
  }, [data, input]);

  if (loading || dataToBeUsed.length === 0) return <h2>Loading...</h2>;
  const keysWithoutResidents = Object.keys(dataToBeUsed[0]).filter((key) => key !== 'residents');

  return (
    <table>
      <thead>
        <tr>
          {keysWithoutResidents.map((key) => <th key={ key }>{ key }</th>)}
        </tr>
      </thead>
      <tbody>
        {dataToBeUsed
          .map((planet) => (
            <tr key={ planet.name }>
              {keysWithoutResidents.map((key) => (
                <td key={ planet[key] }>
                  {planet[key]}
                </td>
              ))}
            </tr>
          ))}
      </tbody>
    </table>
  );
};

export default Table;
