import React, { useContext, useState, useEffect } from 'react';
import StarWarsPlanetsContext from '../../../context/StarWarsPlanetsContext';

const BtnAddFilter = () => {
  const { data, column, comparison,
    numberValue, setState, setNumberValue, setColumnAlreadyUsed, columnAlreadyUsed } = useContext(StarWarsPlanetsContext);
  const [filteredPlanets, setfilteredPlanets] = useState([]);

  const filter = () => {
    let doubleFilteredPlanets = [];
    if (comparison === 'maior que') {
      doubleFilteredPlanets = data.filter((planet) => +planet[column] > +numberValue);
      setfilteredPlanets(doubleFilteredPlanets);
      setNumberValue('');
    }

    if (comparison === 'menor que') {
      doubleFilteredPlanets = data.filter((planet) => +planet[column] < +numberValue);
      setfilteredPlanets(doubleFilteredPlanets);
      setNumberValue('');
    }

    if (comparison === 'igual a') {
      doubleFilteredPlanets = data.filter((planet) => +planet[column] === +numberValue);
      setfilteredPlanets(doubleFilteredPlanets);
      setNumberValue('');
      setColumnAlreadyUsed()
    }
  };

  useEffect(() => {
    setState(filteredPlanets);
  }, [filteredPlanets, setState]);

  return (
    <button
      data-testid="button-filter"
      type="button"
      onClick={ () => filter() }
    >
      Add Filter
    </button>
  );
};

export default BtnAddFilter;
