  return (
    <table>
      <thead>
        <tr>
          {keysWithoutResidents.map((key) => <th key={ key }>{ key }</th>)}
        </tr>
      </thead>
      <tbody>
        {data
          .map((planet) => (
            <tr key={ planet.name }>
              {keysWithoutResidents.map((key) => (
                <td key={ planet[key] }>
                  {planet[key]}
                </td>
              ))}
            </tr>
          ))}
      </tbody>
    </table>
  );
};

export default Table;
