import React from 'react';
import { Button, Container } from '../../globalStyles';
import { 
  InfoSec,
  InfoRow,
  InfoColumn,
  TextWrapper
} from './InfoSection.elements';

const InfoSection = ({ lightBg, imgStart }) => {
  return (
    <>
      <InfoSec
        lightBg={lightBg}
      >
        <Container>
          <InfoRow imgStart={imgStart}>
            <InfoColumn>
              <TextWrapper>
                <TopLine 
                  LightTopLine={lightTopLine}
                >
                  {lightTopLine}
                </TopLine>
                <Heading 
                  lightText={lightText}
                >
                  {headline}
                </Heading>
                <Subtitle
                  lightTextDesc={lightTextDesc}
                >
                  {description}
                </Subtitle>
                <Link to='/sign-up'>
                  <Button
                    big
                    fontBig
                    primary={primary}
                  >
                    {buttonLabel}
                  </Button>
                </Link>
              </TextWrapper>
            </InfoColumn>
          </InfoRow>
        </Container>
      </InfoSec>
    </>
  )
}

export default InfoSection;
