const saveInfoLocalStorage = (name, email, score, assertions) => {
  const coisaqualquerai = {
    player: {
      name,
      assertions,
      score,
      gravatarEmail: email,
    },
  };
  localStorage.setItem('state', JSON.stringify(coisaqualquerai));
};

export default saveInfoLocalStorage;
