import React, { Component } from 'react';
import { arrayOf, objectOf } from 'prop-types';
import { connect } from 'react-redux';
import { Button, Feed, Hits, TotalScore } from '../components/Feedback';
import { PlayerImg, PlayerName, Score } from '../components/Header';

class Feedback extends Component {
  render() {
    const { getQuestions } = this.props;
    const assertions = 4; // Pegar valor do stado global ou na localStorage n達o tenho certeza;
    const totalScore = 0; // Pegar valor do stado global ou na localStorage n達o tenho certeza;
    const qntQuestions = getQuestions.length;
    return (
      <div>
        <header>
          <h1>Feedback</h1>
          <PlayerImg />
          <PlayerName />
          <Score />
        </header>
        <main>
          <Feed
            assertions={ assertions }
          />
          <TotalScore
            totalScore={ totalScore }
          />
          <Hits
            assertions={ assertions }
            qntQuestions={ qntQuestions }
          />
          <Button
            link="/"
            testId="btn-play-again"
            label="Jogar novamente"
          />
          <Button
            link="/ranking"
            testId="btn-ranking"
            label="Ver Ranking"
          />
        </main>
      </div>
    );
  }
}

const mapStateToProps = ({ questions }) => ({
  getQuestions: questions.questions,
  answered: questions.answered,
});

Feedback.propTypes = {
  getQuestions: arrayOf(objectOf).isRequired,
};

export default connect(mapStateToProps)(Feedback);
