const saveInfoLocalStorage = (name, email, score, assertions) => {
  const player = {
    name,
    assertions,
    score,
    gravatarEmail: email,
  };
  localStorage.setItem('state', JSON.stringify(player));
};

export default saveInfoLocalStorage;
