const calculateScore = (timer, difficulty) => {
  const score = 10;
  const hardMultiplyier = 3;
  const mediumMultiplyier = 2;
  switch (difficulty) {
  case 'hard':
    return (score + (timer * hardMultiplyier));
  case 'medium':
    return (score + (timer * mediumMultiplyier));
  case 'easy':
    return (score + timer);
  default:
    return 0;
  }
};

export default calculateScore;
