import React, { Component } from 'react';

class InputEmail extends Component {
  render() {
    const { handleInput } = this.props;
    return (
      <label htmlFor="email-input">
        E-mail
        <input
          type="email"
          id="email-input"
          data-testid="email-input"
          name="email"
          onChange={ handleInput }
        />
      </label>
    );
  }
}

export default InputEmail;
