import React, { Component } from 'react';
import { connect } from 'react-redux';
import { func } from 'prop-types';
import fetchCurrencies from '../../../fetchs/fetchCurrencies';

class ExpenseCurrencyInput extends Component {
  componentDidMount() {
    const { getCurrencies } = this.props;
    getCurrencies();
  }

  render() {
    return (
      <label
        htmlFor="currencies"
      >
        Moeda
        <select
          id="currencies"
        >
          <option>
            BRL
          </option>
        </select>
      </label>
    );
  }
}

ExpenseCurrencyInput.propTypes = {
  getCurrencies: func.isRequired,
};

const mapDispatchToProps = (dispatch) => ({
  getCurrencies: () => dispatch(fetchCurrencies()),
});

// const mapStateToProps = (state) => ({

// })

export default connect(null, mapDispatchToProps)(ExpenseCurrencyInput);
