import React, { Component } from 'react';
import { connect } from 'react-redux';
import { arrayOf, objectOf } from 'prop-types';
import expenseTableRowsNames from './data';

class ExpenseTable extends Component {
  render() {
    const { expenses } = this.props;
    return (
      <table>
        <thead>
          <tr>
            {expenseTableRowsNames.map((name) => <th key={ name }>{name}</th>)}
          </tr>
        </thead>
        <tbody>
          {expenses
            .map(({ id, value, description, currency, method, tag, exchangeRates }) => {
              const { ask, name } = exchangeRates[currency];
              const valueBRL = (+value * +ask).toFixed(2);
              return (
                <tr key={ id }>
                  <td>{description}</td>
                  <td>{tag}</td>
                  <td>{method}</td>
                  <td>{value}</td>
                  <td>{currency}</td>
                  <td>{ask}</td>
                  <td>{valueBRL}</td>
                  <td>{name}</td>
                  <td>
                    <button
                      type="button"
                    >
                      Editar
                    </button>
                    <button
                      type="button"
                    >
                      Excluir
                    </button>
                  </td>
                </tr>
              );
            })}
        </tbody>
      </table>
    );
  }
}

ExpenseTable.propTypes = {
  expenses: arrayOf(objectOf).isRequired,
};

const mapStateToProps = (state) => ({
  expenses: state.wallet.expenses,
});

export default connect(mapStateToProps)(ExpenseTable);
